import sys
from io import BytesIO
import base64
from PIL import Image

bkg_base64 = ''


def convert(filepath: str):
    bkg = Image.open(BytesIO(base64.b64decode(bkg_base64)))
    img = Image.open(filepath).convert('L')
    bkg_pixels = bkg.load()
    img_pixels = img.load()
    for x in range(img.size[0]):
        for y in range(img.size[1]):
            if img_pixels[x, y] == 0:
                bkg_pixels[x, y] = 255
    bkg.save('-dark.'.join(filepath.rsplit('.', 1)))


if __name__ == '__main__':
    convert(sys.argv[1])
